
public class conditionStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x, y;
		
		x = 1;
		y = 2;
		
		if (7 < 1) {
			
			System.out.print("adding 1 and 2 gives ");
			
			
			
			System.out.println(x+y);
		}
		
		else
		{
			
			
		System.out.println("subtracting 1 and 2 gives ");
		System.out.println(x-y);
		
		}
		
		System.out.println("else is skipped");

	}

}
